/* General Styles */
body {
    font-family: 'Poppins', sans-serif;
    margin: 0;
    padding: 0;
    background-color: #f8f8ff;
    color: #333;
    overflow-x: hidden;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

/* Header */
.site-header {
    background: rgba(255, 255, 255, 0.8);
    backdrop-filter: blur(10px);
    position: sticky;
    top: 0;
    z-index: 1000;
    padding: 10px 0;
    border-bottom: 1px solid #eee;
}

.header-content {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.logo {
    font-size: 1.5em;
    font-weight: 700;
    display: flex;
    align-items: center;
    gap: 5px;
}

.logo svg {
    fill: #7f00ff; /* Purple color */
}

.lightning {
    color: #ffc107; /* Orange color */
}

.main-nav ul {
    list-style: none;
    margin: 0;
    padding: 0;
    display: flex;
    gap: 25px;
}

.main-nav a {
    text-decoration: none;
    color: #333;
    font-weight: 600;
    transition: color 0.3s ease;
    display: flex;
    align-items: center;
    gap: 5px;
}

.main-nav a:hover,
.main-nav a.active {
    color: #7f00ff; /* Purple color */
}

.nav-link.active .dot {
    display: inline-block;
    width: 6px;
    height: 6px;
    background-color: #7f00ff; /* Purple color */
    border-radius: 50%;
}

.user-avatar img {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    object-fit: cover;
    border: 2px solid #7f00ff;
}

/* Hero Section */
.hero-section {
    text-align: center;
    padding: 100px 20px;
    position: relative;
    background: linear-gradient(135deg, #ffafbd, #ffc3a0);
    overflow: hidden;
}

.hero-section::before {
    content: '';
    position: absolute;
    top: -50px;
    left: -50px;
    width: 200px;
    height: 200px;
    background: linear-gradient(45deg, #ffc3a0, #ffafbd);
    border-radius: 50%;
    opacity: 0.6;
    filter: blur(30px);
}

.hero-section::after {
    content: '';
    position: absolute;
    bottom: -50px;
    right: -50px;
    width: 250px;
    height: 250px;
    background: linear-gradient(-45deg, #c3a0ff, #afbdff);
    border-radius: 50%;
    opacity: 0.6;
    filter: blur(40px);
}

.background-grid {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: 
        linear-gradient(to right, rgba(255,255,255,0.2) 1px, transparent 1px),
        linear-gradient(to bottom, rgba(255,255,255,0.2) 1px, transparent 1px);
    background-size: 40px 40px;
    opacity: 0.4;
    pointer-events: none;
    z-index: 1;
}

.hero-content {
    position: relative;
    z-index: 2;
}

.ai-badge {
    display: inline-flex;
    align-items: center;
    background: linear-gradient(90deg, #ff6ec4, #7873f5);
    color: white;
    padding: 6px 15px;
    border-radius: 20px;
    font-size: 0.9em;
    margin-bottom: 20px;
    gap: 5px;
}

.ai-badge .dot {
    width: 8px;
    height: 8px;
    border-radius: 50%;
}

.ai-badge .green {
    background-color: #4CAF50;
}

.ai-badge .orange {
    background-color: #FF9800;
}

.external-link {
    font-size: 1.1em;
    margin-left: 5px;
}

.hero-title {
    font-size: 3.5em;
    font-weight: 700;
    margin-bottom: 20px;
    background: linear-gradient(90deg, #ff6ec4, #7873f5);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    color: transparent; /* Fallback for browsers that don't support clip */
}

.hero-description {
    font-size: 1.2em;
    color: #555;
    margin-bottom: 40px;
    line-height: 1.6;
}

.hero-actions {
    display: flex;
    justify-content: center;
    gap: 20px;
}

.btn {
    display: inline-block;
    padding: 12px 30px;
    border-radius: 25px;
    text-decoration: none;
    font-weight: 600;
    font-size: 1.1em;
    transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.btn-primary {
    background: linear-gradient(90deg, #a855f7, #5e3ac2);
    color: white;
    border: none;
}

.btn-primary:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
}

.btn-secondary {
    background: white;
    color: #7f00ff; /* Purple color */
    border: 2px solid #7f00ff;
}

.btn-secondary:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
}

.sparkle, .play {
    font-size: 1.2em;
    margin-left: 5px;
}

/* Demo Section (Placeholder Styling) */
.demo-section {
    padding: 50px 20px;
    background-color: #f0f0f8;
    margin-top: -50px; /* Overlap with hero slightly */
    position: relative;
    z-index: 3;
}

.demo-container {
    display: flex;
    justify-content: center;
    align-items: center;
}

.browser-frame {
    width: 100%;
    max-width: 900px;
    background: #222;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3);
}

.browser-header {
    display: flex;
    align-items: center;
    padding: 10px 15px;
    background: #333;
    color: #bbb;
    font-size: 0.9em;
    border-bottom: 1px solid #444;
}

.browser-dots {
    display: flex;
    gap: 5px;
    margin-right: 15px;
}

.browser-dots .dot {
    width: 10px;
    height: 10px;
    border-radius: 50%;
}

.browser-dots .red { background-color: #ff5f56; }
.browser-dots .yellow { background-color: #ffbd2e; }
.browser-dots .green { background-color: #27c93f; }

.browser-url {
    flex-grow: 1;
    text-align: center;
}

.browser-url .lock {
    margin-right: 5px;
}

.browser-pro {
    margin-left: 15px;
    font-weight: bold;
    color: #ffc107; /* Orange */
}

.browser-content {
    display: flex;
    height: 400px; /* Example height */
}

.design-panel,
.code-panel {
    flex: 1;
    display: flex;
    flex-direction: column;
}

.design-panel {
    background: #7f00ff; /* Purple gradient base */
    background: linear-gradient(135deg, #a855f7, #7f00ff);
    color: white;
}

.code-panel {
    background: #1a1a1a;
    color: #bbb;
    border-left: 1px solid #444;
    overflow-y: auto;
}

.panel-header {
    padding: 10px 15px;
    font-size: 0.9em;
    border-bottom: 1px solid rgba(255,255,255,0.1);
    display: flex;
    align-items: center;
    gap: 8px;
}

.panel-header .dot {
    width: 10px;
    height: 10px;
    border-radius: 50%;
}

.panel-header .green { background-color: #4CAF50; }
.panel-header .purple { background-color: #a855f7; }

.design-content {
    flex-grow: 1;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 20px;
    overflow: hidden;
}

.design-content img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
    /* Add a subtle shadow or border if needed */
}

.code-content {
    flex-grow: 1;
    padding: 15px;
    font-family: Consolas, Monaco, 'Andale Mono', 'Ubuntu Mono', monospace;
    font-size: 0.9em;
    line-height: 1.5;
    white-space: pre-wrap;
    word-wrap: break-word;
}

.code-content code {
    display: block;
}

/* Responsive adjustments */
@media (max-width: 768px) {
    .header-content {
        flex-direction: column;
        gap: 15px;
    }

    .main-nav ul {
        flex-wrap: wrap;
        justify-content: center;
        gap: 15px;
    }

    .hero-title {
        font-size: 2.5em;
    }

    .hero-description {
        font-size: 1em;
    }

    .hero-actions {
        flex-direction: column;
        gap: 15px;
    }

    .btn {
        width: 80%;
        max-width: 300px;
        margin: 0 auto;
    }

    .browser-content {
        flex-direction: column;
        height: auto; /* Auto height for stacked panels */
    }

    .design-panel,
    .code-panel {
        flex-basis: auto; /* Reset flex-basis */
        width: 100%; /* Take full width when stacked */
    }

    .code-panel {
        border-left: none;
        border-top: 1px solid #444;
    }

    .design-content img {
       max-height: 200px; /* Limit height on smaller screens */
    }
}

@media (max-width: 480px) {
    .logo {
        font-size: 1.2em;
    }
    .main-nav ul {
        gap: 10px;
    }
    .hero-title {
        font-size: 2em;
    }
    .ai-badge {
        font-size: 0.8em;
        padding: 5px 10px;
    }
}
